import java.util.Arrays;
public class LongestPallSubString{
    private String s;
    LongestPallSubString(String s){
        this.s = s;
    }
    public void computeLPS(){
        int len = s.length();
        boolean[][] dp = new boolean[len][len];
        for(int i=0; i<len; i++){
            Arrays.fill(dp[i], true);
        }
        int maxLen = 1, start=0;
        for(int i=0; i<len; i++){
            int p=0, q=i;
            for(; p<len && q<len; p++, q++){
                if(p==q){
                    dp[p][q] = true;
                }
                else{
                    if(s.charAt(p)==s.charAt(q) && dp[p+1][q-1]==true){
                        maxLen = i+1;
                        start = p;
                    }
                    else{
                        dp[p][q] = false;
                    }
                }
            }
        }
    /*    for(int i=0; i<len; i++){
            for(int j=0; j<len; j++){
                if(dp[i][j]==true){
                    System.out.print("T ");
                }
                else{
                    System.out.print("F ");
                }
            }
            System.out.println();
        }*/
    //    System.out.println(start+" "+maxLen);
        System.out.println(s.substring(start, start+maxLen));
    }
    public static void main(String[] args){
        String s1 = "abaabc";
        LongestPallSubString obj1 = new LongestPallSubString(s1);
        String s2 ="aaaabbaa";
        LongestPallSubString obj2 = new LongestPallSubString(s2);
        String s3 = "hackerrekcahba";
        LongestPallSubString obj3 = new LongestPallSubString(s3);
        String s4 = "";
        LongestPallSubString obj4 = new LongestPallSubString(s4);
    //    obj1.computeLPS();
    //    obj2.computeLPS();
    //    obj3.computeLPS();
        obj4.computeLPS();
    }
}